ult_support->fetch()) {			$arr_items = array();			$sql = 'SELECT title FROM ' . NV_PREFIXLANG . '_' . $module_data . '_support_rows WHERE bid ='.$view['bid'] . ' ORDER BY weight ASC';			$result = $db->query($sql);			while (list($title_i) = $result->fetch(3)) {				$arr_items[] = $title_i;			}						$view['item'] = implode('&nbsp;&nbsp; ', $arr_items);			$view['add_content'] = NV_BASE_ADMINURL . 'index.php?' . NV_LANG_VARIABLE . '=' . NV_LANG_DATA . '&amp;' . NV_NAME_VARIABLE . '=' . $module_name . '&amp;' . NV_OP_VARIABLE . '=support_content&amp;bid=' . $view['bid'];			$xtpl->assign('VIEW', $view);			$xtpl->parse('main.support.loop');		}		$xtpl->parse('main.support');	}		$xtpl->parse('main');$contents = $xtpl->text('main');include NV_ROOTDIR . '/includes/header.php';echo nv_admin_theme($contents);include NV_ROOTDIR . '/includes/footer.php';